/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 User Control Task
//
// This task is used to control your robot during the user control phase of a VEX Competition.
// You must modify the code to add your own robot specific commands here.
//
/////////////////////////////////////////////////////////////////////////////////////////
void resetClaw() {
    motor[claw]=0;
    wait1Msec(2000);
}

task useClaw ()
{
	int smallDelay=150;
	int plusMinus=10;
	int potPos= SensorValue(clawPot);
	//Start by opening the claw all the way
	startMotor (claw,-95);
	wait1Msec (smallDelay);
	while (abs(SensorValue (clawPot)- potPos)>=plusMinus)
	{
		wait1Msec (smallDelay);
		potPos= SensorValue(clawPot);
	}
	startMotor(claw,-15);
	//Now wait for button push, loop starts here
	while (true)
	{
		waitUntil (vexRT[Btn6U] == 1);
		waitUntil (vexRT[Btn6U] == 0);
		//Closes claw
		potPos= SensorValue(clawPot);
		startMotor (claw,95);
		wait1Msec (smallDelay);
		while (abs(SensorValue (clawPot)- potPos)>=plusMinus)
		{
			wait1Msec (smallDelay);
			potPos= SensorValue(clawPot);
		}
		startMotor(claw,15);
		waitUntil (vexRT[Btn6U] == 1);
		waitUntil (vexRT[Btn6U] == 0);
		potPos= SensorValue(clawPot);
		startMotor (claw,-95);
		wait1Msec (smallDelay);
		while (abs(SensorValue (clawPot)- potPos)>=plusMinus)
		{
			wait1Msec (smallDelay);
			potPos= SensorValue(clawPot);
		}
		startMotor(claw,-15);
	}
}

task usercontrol()
{
	startTask (useClaw);
	while (true)
	{
		if (vexRT[Btn6D] == 1 )
		{
			resetClaw();
		}
	}
}
